mapper.select:
SELECT item.item_group_id
  FROM item
  JOIN item_group
    ON item.item_group_id = item_group.item_group_id
 WHERE item_id in(
                    SELECT DISTINCT item_id
                      FROM Item_Schedule

                        WHERE item_id IS NOT NULL
                       AND start_date >= #{startDate}
                         AND end_date <= #{endDate}
                           AND price <= #{underPrice}
                           AND price >= #{overPrice}
                  )
