mapper.update:
update member
set member_id=#{memberId},email=#{email},password=#{password},nickname=#{nickname},state=#{state},created_at=#{createdAt},
            updated_at=#{updatedAt},deleted_at=#{deletedAt},email_confirm=#{emailConfirm},point=#{point},account=#{account},
            grade=#{grade},profile_img=#{profileImg},gender=#{gender},birthday=#{birthday},mobile=#{mobile},address=#{address},
            sns_info=#{snsInfo}
where member_id=#{memberId}
